import React from "react";

import styles from "./styles.module.scss";

export enum TagType {
  ACTIVE = "ACTIVE",
  PASSIVE = "PASSIVE",
  HEROIC = "HEROIC",
  ABILITY = "ABILITY",
}
interface TagProps extends React.HTMLAttributes<HTMLDivElement> {
  type: TagType;
  isQuest: boolean;
  text: string;
}

export function Tag(tagProps: TagProps) {
  const { type, isQuest, text } = {
    ...{
      isQuest: false,
    },
    ...tagProps,
  };

  const hasArrowIcon = type === TagType.ABILITY && !isQuest;

  return (
    <div className={styles.tagContainer}>
      {hasArrowIcon && (
        <img
          className={styles.arrowIcon}
          src="data:image/png;base64,"
        />
      )}
      <div className={`${styles.tag} ${styles[type]}`}>
        {isQuest && (
          <img
            className={styles.questIcon}
            src="data:image/png;base64,"
          />
        )}
        <div
          className={`${styles.tagText} ${hasArrowIcon && styles.hasArrowIcon}`}
        >
          {text}
        </div>
      </div>
    </div>
  );
}
